static uint8_t hex_digit(uint8_t x) {
    if (x < 10) {
        return x + '0';
    } else {
        return x - 10 + 'A';
    }
}

static void print_hex(char *dst, uint8_t x) {
    dst[0] = hex_digit(x >> 4);
    dst[1] = hex_digit(x & 0xf);
}

static void show_packet(void) {
    uint16_t len = ETH_RX_LEN + 1;
    uint8_t color = crc_check_relaxed(ETH_BUF, ETH_BUF + len) ? COLOR(COLOR_LIGHT_GREEN, COLOR_BLACK) : COLOR(COLOR_RED, COLOR_BLACK);
    vga_clear(color);
    uint8_t *dst = VGA_CHAR_SEG;
    uint8_t *end = ETH_BUF + len;
    uint8_t col = 0;
    uint8_t row = 0;
    for (uint8_t *src = ETH_BUF; src != end; ++src) {
        print_hex(dst, *src);
        if (col == 26) {
            col = 0;
            dst += 128 - 26 * 3;
            row += 1;
        } else {
            col += 1;
            dst += 3;
        }
        if (row == VGA_ROWS) {
            break;
        }
    }
    ETH_RX_ARM();
}

