static void send_packet(void) {
    eth_init();
    eth_transmit(packet, sizeof(packet));
    // for (uint16_t i = 0; i != sizeof(packet); ++i) {
    //     ETH_BUF[i] = bitmix[packet[i]];
    // }
    // ETH_TX_START();
    // packet[1023] ^= 0x80;
    sprintf(VGA_CHAR_SEG + VGA_OFFSET(0, 29), "SENT");
}

void main(void) {
    eth_init();
    memset(ETH_BUF, 0xaa, 1024);
    vga_clear(COLOR(COLOR_GRAY, COLOR_BLACK));
    while (true) {
        uint8_t key_event = ps2_get_key_event();
        if (ETH_SR & ETH_SR_RX_FULL) {
            show_packet();
        }
        if (key_event == PS2_KEY_ENTER) {
            send_packet();
        }
    }
}

