void render_map(const uint8_t *map) {
    uint16_t i = 0;
    for (uint8_t r = 0; r != QP_HEIGHT; ++r) {
        for (uint8_t c = 0; c != QP_WIDTH; c += 8) {
            int8_t val = map[i];
            for (uint8_t bit = 0; bit != 8; ++bit) {
                qp_set(c + bit, r, val < 0);
                val <<= 1;
            }
            ++i;
        }
    }
    qp_render();
}
